const {
  nomeCompleto,
  email,
  senha,
  admin
} = gerarDadosUsuario(); //gerando o objeto com dados do usuário
userActions.cadastrarUsuario(nomeCompleto, email, senha, admin) // Primeiro cadastro para garantir que o email já existe
.then(() => {
  // Tenta cadastrar novamente com o mesmo email (espera erro)
  userActions.cadastrarUsuario(nomeCompleto, email, senha, admin).should(response => {
    // Valida que o status é 400 e a mensagem de erro
    expect(response.status).to.equal(400);
    expect(response.body.message).to.equal('Este email já está sendo usado');
  });
});

